import React, {useState} from 'react';

const welcome = (props)=>{
    let name;
    return(
        <div>
            <h1>Looks like you're new around here..</h1>
            <h2>Let's get you set up</h2>
            <h2>We'll start with a name</h2>
            <form onSubmit={(e)=>{props.handleSubmit(e,name)}}>
                <input onChange={(e)=>{name=e.target.value}}></input>
                <button>That's My Name</button>
            </form>
        </div>
    );
}

const question1 = (props)=>{

    return(
        <div>
            <h1>That's a great name {props.name}</h1>
        </div>
    )
}



class SignUp extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            name: "user",
            page: 0,

        };
    }

    getName = ()=>{
        return (this.state.page);
    }
    changePage = ()=>{
        let page = this.state.page;
        this.setState({page:page+1});
    }

    handleTextChange = (e,changeWith)=>{
        e.preventDefault();
        this.setState({toChange:changeWith});
        console.log(changeWith);
        this.changePage();
    }

    render(){
        let pages= [
            welcome({handleSubmit: this.handleTextChange}),
            question1({name:this.state.name})
        ]

        let i = 0;
        return(
            <div>
            {pages[this.state.page]}
            </div>
        )
    }
}

export default SignUp;
