import React, {useState} from 'react';

const welcome = (props)=>{
    let name;
    return(
        <div>
            <h1>Looks like you're new around here..</h1>
            <h2>Let's get you set up</h2>
            <h2>We'll start with a name</h2>
            <form onSubmit={(e)=>{props.handleSubmit(e,"name",name)}}>
                <input onChange={(e)=>{name=e.target.value}}></input>
                <button>That's My Name</button>
            </form>
        </div>
    );
}

class SignUp extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            name: "user",
        };
    }

    handleTextChange(e,toChange,changeWith){
        e.preventDefault();
        console.log(e);
        this.setState({toChange:changeWith});
    }

    render(){
        return(
            <div>
                {welcome({handleSubmit: this.handleTextChange})}
            </div>
        )
    }
}

export default SignUp;
