import React, { Component } from 'react'

class Main extends Component {
  render() {

    const userEnum = {
        NEW: 1,
        MALE: 2,
        FEMALE: 3
    }
        let content;
        let welcome;
        let userState;
        if(!this.props.user.handle){
            userState = userEnum.NEW;
            welcome = "You don't have an account yet? Sign up below!";
        }else if (this.props.user.male){
            userState = userEnum.MALE;
            welcome = "Your stats as a Male";
            console.log(userState);
        }else{
            userState = userEnum.FEMALE;
            welcome = "your stats as a Female";
        }

    //Page for sign up
    let signUp = <div>
        <h1>{welcome}</h1>
        <h2>{window.web3.utils.fromWei(this.props.loveCoinBalance,"Ether")} LUV</h2>
        <h2>{this.props.user.score} Score</h2>
        <form onSubmit={(event)=>{
            event.preventDefault();
            let isMale;
            if(this.maleOption.checked){
                isMale = true;
            }else if(this.femaleOption.checked){
                isMale = false;
            }
            this.props.join(isMale,this.handle.value);
        }}>
            <label>Instagram Handle: </label>
            <input
                type="text"
                ref = {(handle)=>{this.handle = handle}}
            />
            <br></br>
            <input type="radio" 
            id="male" name="sex" 
            value="male"
            ref = {(maleOption)=>{this.maleOption = maleOption}}
            />
            <label htmlFor="male">Male</label><br></br>

            <input 
            type="radio" 
            id="female" 
            name="sex" 
            value="female"
            ref = {(femaleOption)=>{this.femaleOption=femaleOption}}
            />
            <label htmlFor="female">Female</label><br></br>
            <button type = "submit">Join!</button>
        </form>
    </div>
    //END SIGN UP PAGE

    //DECIDE PAGE
    if(userState=userEnum.NEW){
        content = signUp;
    }else if (userState = userEnum.MALE){
        content = <h1>Hi there sir</h1>
    }
    return(
      <div>
      {content}
      </div>
    );
  }
}

export default Main;
