import React, { Component } from 'react'

class Main extends Component {

  render() {
      let sex = "Female";
      if(this.props.user.sex){
          sex = "Male";
      }
    return (
      <div>
        <h1>Your Stats as a {sex}</h1>
        <h2>{window.web3.utils.fromWei(this.props.loveCoinBalance,"Ether")} LUV</h2>
        <h2>{this.props.loveScore} Score</h2>
      </div>
    );
  }
}

export default Main;
