import React, {useState} from 'react';

const welcome = (props)=>{
    let name;
    return(
        <div>
            <h1>Looks like you're new around here..</h1>
            <h2>Let's get you set up</h2>
            <h2>We'll start with a name</h2>
            <form onSubmit={(e)=>{props.handleSubmit(e,name)}}>
                <input onChange={(e)=>{name=e.target.value}}></input>
                <button>That's My Name</button>
            </form>
        </div>
    );
}

const question1 = (props)=>{
    let option;
    return(
        <div>
            <h1>That's a great name {props.name}</h1>
            <h2>Are you a fella or a lady?</h2>
            <form onSubmit={(e)=>{props.handleSex(e,option)}}>
            <div className="radio">
                <label>
                    <input type="radio" value="male" onChange={(e)=>{option = e.target.value}}></input>
                    Male
                </label>
                <label>
                    <input type="radio" value="male" onChange={(e)=>{option = e.target.value}}></input>
                    Female
                </label>
            </div>

                <button>Submit</button>
            </form>
        </div>
    )
}



class SignUp extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            name: "user",
            page: 0,
            sex: true

        };
    }

    getName = ()=>{
        return (this.state.page);
    }
    changePage = ()=>{
        let page = this.state.page;
        this.setState({page:page+1});
    }

    handleNameChange = (e,changeWith)=>{
        e.preventDefault();
        this.setState({name:changeWith});
        this.changePage();
    }

    handleSexChange = (e,changeWith)=>{
        e.preventDefault();
        this.setState({sex:changeWith});
        alert(self.state.sex);
        //this.changePage();
    }

    render(){
        let pages= [
            welcome({handleSubmit: this.handleNameChange}),
            question1({name:this.state.name,
                        handleSex: this.handleSex})
        ]

        let i = 0;
        return(
            <div>
            {pages[this.state.page]}
            </div>
        )
    }
}

export default SignUp;
