import React, { Component } from 'react'

class Main extends Component {

    userEnum = {
        NEW: 0,
        MALE: 1,
        FEMALE: 2
    }
  render() {
      let welcome;
      let userState;
      if(!this.props.user.handle){
          userState = userEnum.NEW;
        welcome = "You don't have an account yet? Sign up below!";
      }else if (this.props.user.male){
          userState = userEnum.MALE;
          welcome = "Your stats as a Male";
      }else{
          userState = userEnum.FEMALE;
          welcome = "your stats as a Female";
      }
    return (
      <div>
        <h1>{welcome}</h1>
        <h2>{window.web3.utils.fromWei(this.props.loveCoinBalance,"Ether")} LUV</h2>
        <h2>{this.props.user.score} Score</h2>
      </div>
    );
  }
}

export default Main;
