import React from 'react'

class Feed extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            index: 0,
            loading: true,
            feed: []
        }
    }

    async componentDidMount(){
        this.setState({loading:true});
        let _feed = await this.props.getFeed();
        this.setState({feed:_feed});
        this.setState({loading:false});

    }
    //Helper function to dry up code
    present(){
        let user = this.state.feed[this.state.index];
        console.log(user);
        return user.handle;
    }

    handleLike = (e)=>{
        e.preventDefault();
        if(this.props.male){
            this.props.likeFunc(this.state.index);
        }else{
            this.props.acceptFunc(this.state.index);
        }
        this.next();
    }

    next(){
        this.setState({index:this.state.index+1});
    }


    render(){
        let content;

        if(this.state.loading ===true){
            content = <div>
                <h1>Loading...</h1>
            </div>
        }else{
            if(this.state.index >= this.state.feed.length){
                content = 
                    <div>
                        <h1>Uh oh!</h1>
                        <h2>Looks like your feed is empty. Refresh  the page to reload</h2>
                </div>


            }else{
                if(this.props.male){
                    content =
                        <div>
                            <h1>{this.present()}</h1>
                            <button onClick={this.handleLike}>Like</button>
                            <button onClick = {this.next}>Skip</button>
                        </div>


                }else if(this.props.male === false){
                    content =
                        <div>
                            <h2>You have a like from:</h2>
                            <h1>{this.presentHandle()}</h1>
                            <button onClick={this.handleLike}>Accept</button>
                            <button onClick = {this.next}>Skip</button>                       
                        </div>


                }

            }

        }
        return(
            <div>
            {content}
            </div>
            )
    }
}

export default Feed;
