import React from 'react'
class Feed extends React.Component{

    feedList;
    maxSize;
    constructor(props){
        super(props);
        this.state = {
            index: 0,
            loading: true
        }
    }

    setupFeed = async()=>{
        self.setState({loading:true});
        await this.props.getFeed();
        self.setState({loading:false});
    }

    //Helper function to dry up code
    presentHandle(){
        return this.feedList[this.state.index];
    }

    handleClick = (e)=>{
        e.preventDefault();
        if(this.props.male){
            this.props.likeFunc(this.state.index);
        }else{
            this.props.acceptFunc(this.state.index);
        }

        this.setState({index:this.state.index+1});
    }


    render(){
        let content;
        if(this.state.index == this.maxSize){
            content = 
                <div>
                    <h1>Uh oh!</h1>
                    <h2>Looks like your feed is empty. Refresh  the page to reload</h2>
            </div>


        }else{
            if(this.props.male){
                content =
                    <div>
                        <h1>{this.presentHandle()}</h1>
                        <form onSubmit={this.handleClick}>
                            <button>Like</button>
                            <button>Skip</button>
                        </form>
                    </div>


            }else if(this.props.male === false){
                content =
                    <div>
                        <h2>You have a like from:</h2>
                        <h1>{this.presentHandle()}</h1>
                        <form onSubmit={this.handleClick}>
                            <button>Accept</button>
                            <button>Skip</button>                       
                        </form>
                    </div>


            }

        }
        return(
            <div>
            {content}
            </div>
            )
    }
}

export default Feed;
