import './Main.css';
//import { findAllByTestId } from '@testing-library/react';
import React from 'react'

import SingleButton from './SingleButton';
import Feed from './Feed';
import { Button } from 'bootstrap';

class Main extends React.Component{
  constructor(props){
    super(props);
    this.connect = this.connect.bind(this);
    this.signUp = this.signUp.bind(this);
    this.likeFunc = this.likeFunc.bind(this);
    this.acceptFunc = this.acceptFunc.bind(this);

    this.state = {
      page: 0,
      connected: this.props.isConnected,
      loading: false,
      score: 10,
      isMale: this.props.user.male,
      isUser: this.props.user.isUser
    };

  }
  //pretned list of female users
  //NOTE: Smart Contract right now does not have a list of female users. That will need to be added. This will actually be user objects
  femaleUsers = ["@Samantha","@FentanylAdd1ct43","@DojaCat","@DoesntUnderstandTheFineLineBetweenRudeAndFlirty","@BigTattas","@YourDadsSideChick"];

  //pretend lsit of our female user's male likes. This will actually be user objects
  //NOTE: no array of this is kept either except people that "have liked". Merging code will have to itterate though that and sort out ones already liked
  femaleLikes = ["@Billys_Hard","@ANiceGuy","@Mysoginsit","@r/memes_admin32"];

  //pretnet list of our heros existing mataches. This will actually be user objects
  //NOTE: We will need another mapping for this. Each user's matches. Will be used for NOTE above to sort out existing matches
  usersMatches = ["@SommerRay","@JohnnySinsTM","@MaddisonBeer","@The_Real_Meghan_Fox"];

  async connect(e){
    e.preventDefault();
    this.setState({loading:true});
    await this.props.connectBlockchain();
    this.setState({connected:true,loading:false});
    console.log(this.state.isUser);
    if(this.state.isUser){
      this.setState({page:2});
    }else{
      this.setState({page:1});
    }

  }

  signUp = async(e,sex,handle)=>{
    e.preventDefault();
    await this.props.join(sex,handle);
    await this.setState({page:2});    

  }

  likeFunc(who){    //For now who is an index. In reality it will be an address
    delete this.femaleUsers[who]
    console.log("like!");
  }

  acceptFunc(who){  //What this guy says ^
    delete this.femaleLikes[who]
    console.log("Accept!");
  }

  burnTokens(e,n){
    e.preventDefault();
    if(this.props.balance < n){
      alert("Not enough tokens");
    }else{
        //Call the burn tokens function. Needs to be added. But converts LUV to score
        console.log("LUV burned");
    }
  }

  render(){

    let content;
    if(!this.state.connected ||this.state.loading){
        <h1>Loading Please wait...</h1>
    }
    <h1>Loading. Please wait...</h1>
    //Connect Wallet Page   [0]
    if(this.state.page == 0){ 
      content =     
      <SingleButton 
      title="LoveCoin"
      subtitle="The worlds first dating cryptocurrency"
      btn="Connect Wallet"
      foo={this.connect}
      />
    }
    //Sign Up Page    [1]
    if(this.state.page == 1){
      content = 
      <SingleButton
      title="Looks like you havn't signed up..."
      btn="Sign Up"
      foo={this.signUp}
      />
    }
    //Feed            [2]
    if(this.state.page == 2 ){
      let feedList;
      if(this.state.isMale){ //If user is male
        feedList = this.femaleUsers;
      }else{
        feedList = this.femaleLikes;
      }
      content = 
      <Feed
      male={this.state.isMale}
      feedList = {feedList}
      likeFunc = {this.likeFunc}
      acceptFunc = {this.acceptFunc}
      />
    }

    let statsPanel;
    if(this.state.signedIn){
      statsPanel=<div>
        <h1>User Stats</h1>
        <form onSubmit = {(e)=>{
          this.burnTokens(e,10);
        }}>
          <button>Self Love</button>
        </form>
        <p>Score: {this.state.score}</p>    
        <p> Balance: {this.props.balance} LUV</p> 
        <h1>Your Matches</h1>
        {this.usersMatches.map(match=>(
                    <p>{match}[{12}]</p>
                ))}

      </div>
    }else{
      statsPanel=<div>
        <button>LUV on uniswap</button>
      </div>
    }

    return (
      <div className="App">
      <div className="row">
        <div className="col-md-10">
          <header className="App-header">
            {content}
          </header>
        </div>
        <div className="col-md-2">
          <div>
            <h1>Global Stats</h1>
            <p>Global Male Scores: {this.props.globals.male_scores}</p>
            <p>Global Female Scores: {this.props.globals.female_scores}</p>
            <p>Users: 2</p>
            <p>LUV price: 0.0001 ETH</p>
          </div>
            {statsPanel}
        </div>
      </div>
      </div>
    );
  }

}

export default Main;
